import { useState } from "react";
import FormField from "../../../UIComponents/FormField";
import { loginCliente } from "../../../servicios/restCliente";

function Login() {
  const [formData, setFormData] = useState({
    login: "",
    password: "",
  });
  function SubmitForm(ev) {
    //Se envían los datos del formData a la BD para comprobar que existe el usuario y su contraseña coincide
    ev.preventDefault();
    try{
      var _respuestaServer = loginCliente(formData);
      console.log('Respuesta del server ante el registro de datos...',_respuestaServer);
    }catch(error){
      console.log(error);
    }
  }
  function HandlerChangeEvents(name, value) {
    setFormData({
      ...formData,
      [name]: value,
    });
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <form className="row g-3" onSubmit={SubmitForm}>
            {[
              {
                id: "login",
                value: formData.email,
                type: "text",
                name: "login",
                label: "Nombre de usuario",
                placeholder: "Mi nombre de usuario",
                validators: {
                  pattern: [
                    "^(?=.*\\d)?(?=.*[\u0021-\u002b\u003c-\u0040])?(?=.*[A-Z])?(?=.*[a-z])\\S{3,}$",
                    "* formato de login incorrecto, al menos 3 letras MINS",
                  ],
                  minlength: [
                    3,
                    "* el login debe contener al menos 3 caracteres",
                  ],
                },
              },
              {
                id: "password",
                value: formData.password,
                type: "password",
                name: "password",
                label: "Contraseña",
                validators: {
                  required: [true, "* Contraseña es obligatoria"],
                  pattern: [
                    "^(?=.*\\d)(?=.*[\u0021-\u002b\u003c-\u0040])(?=.*[A-Z])(?=.*[a-z])\\S{8,}$",
                    "* la contraseña debe contener MAYS,MINS, digito y otro caracter",
                  ],
                  minlength: [
                    8,
                    "* la contraseña debe tener al menos 8 caracteres",
                  ],
                },
              },
            ].map((elemento) => (
              <div className="col-6 p-3 text-secondary" key={elemento.name}>
                <FormField
                  id={elemento.id}
                  type={elemento.type}
                  value={elemento.value}
                  name={elemento.name}
                  label={elemento.label}
                  validators={elemento.validators}
                  onChangeInParent={HandlerChangeEvents}
                ></FormField>
              </div>
            ))}
            <div className="col-12">
              <button type="submit" className="btn btn-primary btn-lg">
                Iniciar Sesión
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
export default Login;
