import { Link, Outlet, useLoaderData } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";

function Layout() {
  let _listaCategorias = useLoaderData();
  return (
    <>
      <Header />
      <div className="row">
        <aside className="col-3">
          <h5>Categor√≠as</h5>
          <div className="list-group">
            {_listaCategorias.map((categoria) => (
            <Link key={categoria.IdCategoria} to={`/Tienda/Libros/${categoria.IdCategoria}`} className="list-group-item list-group-item-action" >
              {categoria.NombreCategoria}
            </Link>
            ))}
        </div>
        </aside>
        <div className="col-8">
      <Outlet />
      </div>
      </div>
      <div className="row">
      <Footer />
      </div>
    </>
  );
}

export default Layout;
