import { useLoaderData } from "react-router-dom";
function Libros(){
    let _libros = useLoaderData();
    return(
        <div className="row">
            {
                _libros.map((libro, index) => (
                    <div className="col-3" key={libro.ISBN13}>
                        <div className="card">
                            <img src={libro.UrlImagen} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">{libro.Titulo}</h5>
                                <p className="card-text">{libro.Autores}</p>
                                <a href="#" className="btn btn-primary">Comprar</a>
                            </div>
                        </div>
                    </div>
                ))
            }
        </div>
    )
}
export default Libros;
