import { Link, useLoaderData } from "react-router-dom";
function Libros(){
let _listaLibros = useLoaderData();
function addLibroToPedido(){
  console.log("Añadir libro al pedido");
}
console.log("Lista de libros", _listaLibros);
    return (
      <div className="container">
        <div className="row">
          {_listaLibros.map((libro) => (
            <div className="col-4 text-center">
              <Link to={"/Tienda/MostrarLibro/"+libro.ISBN13}>
                <img src={libro.ImagenLibroBASE64} alt="" width="auto" height="200" />
              </Link>
              <button className="btn btn-primary btn-sm" onClick={addLibroToPedido}>Comprar</button>
            </div>
          ))}
        </div>
      </div>

    );

};
export default Libros;
