import { useLoaderData, Link } from "react-router-dom";
import { usePedidoContext } from "../../../App";
import { useState } from "react";

function Libros(){
    
    const {pedido, setPedido}=usePedidoContext();
    let _listaLibros=useLoaderData(); //<----- recupero libros del loader del componente asociado al path...
    //Funcion para agregar un item al pedido
    function agregarItemPedido(ev){
        let _isbn13=ev.target.getAttribute("marcador");
        let _libro=_listaLibros.find(item=>item.ISBN13===_isbn13);
        let _pedido=pedido;
        //Comprobamos si el libro ya esta en el pedido
        let _elementoPedido=_pedido.elementosPedido.find(item=>item.libro.ISBN13===_isbn13);
        if(_elementoPedido===undefined){
            //Si no esta lo agregamos
            _elementoPedido={
                libro: _libro,
                cantidad: 1
            }
            _pedido.elementosPedido.push(_elementoPedido);
        }else{
            //Si esta lo incrementamos
            _elementoPedido.cantidad++;
        }
        //Actualizamos el pedido
        _pedido.subtotalPedido+=_libro.Precio;
        _pedido.gastosPedido=2;
        _pedido.totalPedido=_pedido.subtotalPedido+_pedido.gastosPedido;
        setPedido({..._pedido});
        console.log(pedido);
    }
    return (
        <div className="container">
            <div className="row">
                {
                    _listaLibros.map(
                        (item) => {
                            return(
                                <div className="col-4" key={item.ISBN13}>
                                    <div className="mb-3" style={{maxWidth: "540px"}} id={"cardLibro-" + item.ISBN13}>
                                        <div className="row g-0">
                                            {/*<!-- columna para miniimagen del libro y boton comprar--> */}
                                            <div className="col-md-4 text-center" style={{height: "170px"}}>
                                                <div className="w-100" style={{height: "80%"}}>
                                                    <Link to={ "/Tienda/MostrarLibro" + item.ISBN13} >
                                                        <img className="img-fluid rounded-start rounded-end" src={item.ImagenLibroBASE64} alt="..."/>
                                                    </Link>
                                                </div>
                                                <button className="btn btn-primary btn-sm" marcador={item.ISBN13} onClick={agregarItemPedido} >
                                                    Comprar...
                                                </button>
                                            </div>

                                            {/*<!-- columna para titulo del libro, autores, editorial, pags y precio--> */}
                                            <div className="col-md-8">
                                                <div className="ms-3">
                                                    <h6 className="card-title" style={{height: "50px"}}><Link to={"/Tienda/MostrarLibro/" + item.ISBN13} className="text-decoration-none" >{item.Titulo}</Link></h6>
                                                    <div className="card-text">{item.Autores}</div>
                                                    <div className="card-text">{item.Editorial}</div>
                                                    <div className="card-text"><small className="text-muted">{item.NumeroPaginas}  páginas</small></div>
                                                    <div className="card-text"><strong>{item.Precio} €</strong></div>
                                                </div>
                                            </div>

                                        </div>
                                    </div>                    
                                </div>
                            )
                        }
                    )
                }
            </div>
        </div>
    );
}
export default Libros;
