//modulo donde exporto funciones javascript para hacer peticiones ajax al servicio RESTFULL montado sobre NODEJS
export function registrarCliente({nombre, apellidos, email, login, password, telefono}){
    if(login===''){
        login = email;
    }
        var petAjax = new XMLHttpRequest();
        
        petAjax.open('POST','http://localhost:5000/api/Cliente/Registro');
        petAjax.setRequestHeader('Content-Type','application/json');
        
        petAjax.addEventListener('readystatechange',()=>{
            if(petAjax.status === 200 && petAjax.readyState === 4){
                var respuesta = JSON.parse(petAjax.responseText);
                return respuesta;
            }else{
                return {
                    codigo:1,
                    mensaje:'Algo fue mal en la pet ajax al servicio de registrar cliente...'
                }
            }
        })

        petAjax.send(JSON.stringify({nombre, apellidos, email, login, password, telefono}));
}

export async function loginCliente(login, password){
    const query = `?login=${login}&password=${password}`;
    const response = await fetch('http://localhost:5000/api/Cliente/Login',query);
    if(response.ok){
        const cliente = await response.json();

        console.log(cliente)
    }else{
        console.error("ERROR",response.status,response.statusText)
    }
    
    
}
