import { useLoaderData } from "react-router-dom";
function MostrarLibro(){
    let libro=useLoaderData();
    return(
        <div>
            <h1>Mostrar Libro</h1>
            <div className="row">
                <div className="col-4">
                    <img src={libro.ImagenLibroBASE64} alt="Imagen del libro" className="img-fluid"/>
                </div>
                <div className="col-8">
                    <h2>{libro.Titulo}</h2>
                    <h3>{libro.Autores}</h3>
                    <h4>{libro.Editorial}</h4>
                    <h5>{libro.NumeroPaginas} páginas</h5>
                    <h5>{libro.Precio} €</h5>
                    <p>{libro.Sinopsis}</p>
                </div>
            </div>
        </div>
    )
}
export default MostrarLibro;
