import { useState } from "react";

function Registro() {
  const [formData, setFormData] = useState({
    email: "mio@mio.es",
    repemail: "mio@mio.es",
    password: "",
    reppassword: "",
    nombre: "mi nombre",
    apellidos: "mis apellidos",
    login: "mi login",
    telefono: "000 11 22 33",
  });

  function HandlerChangeEvents(ev) {
    setFormData({
      ...formData,
      [ev.target.name]: ev.target.value,
    });
  }

  function comprobarEmail() {
    const plantillaEmail = /^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/;

    const email = document.getElementById("email");
    const repEmail = document.getElementById("repEmail");

    if (
      plantillaEmail.test(email.value) &&
      plantillaEmail.test(repEmail.value) &&
      email.value === repEmail.value
    ) {
      setEmail(email.value);
      return true;
    } else {
      alert("email no validado");
    }
  }

  function comprobarPass() {
    const plantillaPass = /^[a-zA-Z0-9!@#$%^&*]{6,}$/;

    const pass = document.getElementById("password");
    const repass = document.getElementById("rePassword");

    if (
      plantillaPass.test(pass.value) &&
      plantillaPass.test(repass.value) &&
      pass.value === repass.value
    ) {
      setPassword(pass.value);
      return true;
    } else {
      alert("contraseña no validada");
    }
  }

  function comprobarNomApe() {
    const plantillaNomApe = /^[\w \s À-ÿ\u00f1\u00d1]{3,100}$/;

    const nombre = document.getElementById("nombre");
    const apellidos = document.getElementById("apellidos");

    if (
      plantillaNomApe.test(nombre.value) &&
      plantillaNomApe.test(apellidos.value)
    ) {
      setNombre(nombre.value);
      setApellidos(apellidos.value);
      return true;
    } else {
      alert("Nombre y apellidos no validados");

      return false;
    }
  }

  function comprobarNick() {
    const plantillaNick = /.{3,}/;
    const nick = document.getElementById("nick");
    const email = document.getElementById("email");
    console.log(nick.value);
    console.log(email.value);
    if (!plantillaNick.test(nick.value)) {
      alert("Nick no permitido. Se utilizará como nick la cuenta de email");
      nick.value = email.value;
      setNick(email.value);
      console.log(nick.value);
    } else {
      setNick(nick.value);
    }
  }

  function submitForm(ev) {
    if (comprobarEmail() && comprobarPass() && comprobarNomApe()) {
      comprobarNick();
      const nick = document.getElementById("nick");
      const valoresRegistro = {
        email,
        password,
        nombre,
        apellidos,
        nick,
        telf,
      };
      valoresRegistro.nick = nick.value;
      console.log("valores a mandar al servidor...", valoresRegistro);
      ev.preventDefault();
    } else {
      ev.preventDefault();
    }
  }

  return (
    <div className="container p-5 border w-auto">
      <h1>Regístrate en agapea.com</h1>
      <p>
        Tienes tres opciones para registrarte en <strong>agapea.com</strong>y
        disfrutar de nuestros servicios y ventajas.
      </p>
      <p>
        Puedes hacerlo a través de las redes sociales en
        <strong> 4 segundos</strong>. O registrarte con nosotros, no tardarás
        más de <strong>40 segundos</strong> en hacerlo. Solo tendrás que
        rellenar el siguiente formulario con tus datos.
      </p>
      <form className="row col-12" method="post" onSubmit={submitForm}>
        <legend className="text-center col-12 ">Registro con agapea.com</legend>
        <div className="col-6 p-3 text-secondary">
          <label for="email" className="form-label">
            Correo electrónico
          </label>
          <br></br>
          <input
            className="w-100 form-control bg-dark-subtle"
            id="email"
            name="email"
            type="text"
            value={email}
            placeholder={email}
            onChange={(ev) => HandlerChangeEvents}
          />
        </div>
        <div className="col-6 p-3 text-secondary">
          <label for="repEmail" className="form-label">
            Repetir correo electrónico
          </label>
          <br></br>
          <input
            className="w-100 form-control bg-dark-subtle"
            id="repEmail"
            name="repEmail"
            type="text"
            value={repemail}
            placeholder={repemail}
            onChange={(ev) => HandlerChangeEvents}
          />
        </div>
        <div className="col-6 p-3 text-secondary">
          <label for="password" className="form-label">
            Contraseña
          </label>
          <br></br>
          <input
