import { Link, Outlet, useLoaderData } from "react-router-dom";
import { recuperarCategorias } from "../../../servicios/restTienda";
import Header from "./Header";
import Footer from "./Footer";

function Layout() {
  let _listaCategorias = useLoaderData();
  let _categorias = recuperarCategorias(request,{idcategoria: "padres"});
  console.log("Lista de categorias", _listaCategorias);
  return (
    <>
      <Header />
      <div className="row">
      <aside>
      {_categorias.map((categoria) => (
        <Link key={categoria.IdCategoria} to={`/Tienda/Libros/${categoria.IdCategoria}`}>
          {categoria.NombreCategoria}
        </Link>
      ))}
      </aside>
      </div>
      <Outlet />
      <Footer />
    </>
  );
}

export default Layout;
